    public int SumOfEvenNumbersInRange(int start, int end, IList&lt;int&gt; elements)
    {
        int CreateSum(int current, int sum)
        {
            if (current &gt; end)
            {
                return sum;
            }
            else
            {
                elements.Add(current);
                return sum + current + CreateSum(current + 2, sum);
            }
        }
    
