        });
      }
    
      @override
      Widget build(BuildContext context) {
        return Scaffold(
          appBar: AppBar(
            title: FutureBuilder(
              future: _title,
              builder: (context, snapshot) {
                if (snapshot.hasData) {
                  return Text(&#39;${snapshot.data}&#39;);
                } else if (snapshot.hasError) {
                  return Text(&#39;Default Title&#39;);
                }
                return Text(&#39;Loading...&#39;);
              },
            ),
