            {0, 1, 1, 0},   //   0--&gt;1--&gt;3
            {0, 0, 0, 1},   //   |       ^
            {0, 0, 0, 1},   //   v       |
            {0, 0, 0, 0}    //   2-------+
        };
        int indegrees[n];
        bool visited[n];
        memset(&amp;indegrees, 0, sizeof(*indegrees) * n);
