        for (int i = 0; i &lt; n; i++) {
            for (int j = 0; j &lt; n; j++) {
                indegrees[i] += adjacency_matrix[j][i];
            }
        }
    
        for (int i = 0; i &lt; n; i++) {
            for (int j = 0; j &lt; n; j++) {
                if (!indegrees[j] &amp;&amp; !visited[j]) {
                    visited[j] = true;
