  	ja	invalid
  	
  	mov	 al, [rsi+rax-2]	; RAX = # of characters entered
  	
  	cmp	 al, &#39;.&#39;
  	jz	valid
  	cmp	 al, &#39;?&#39;
  	jz	valid
  	cmp	 al, &#39;!&#39;
  	jnz	Error
