                    &lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;item[&lt;?php echo $i; ?&gt;][grade]&quot; placeholder=&quot;Grade&quot;&gt;&lt;/td&gt;
                &lt;/tr&gt;
