&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    function makeLookup(list, map=new Map, prefix=&quot;&quot;) {
        for (let obj of list) {
            map.set(prefix + obj.id, obj);
            if (obj.items) makeLookup(obj.items, map, prefix + obj.id + &quot;,&quot;);
        }
        return map;
    }


