    }
    
    public class Day : BaseTime
    {
    	// add your custom code for Day
    	// No need for recreating them, since it&#39;s already inherited from the base 
    }
    
    class Program
    {
    	public static BaseTime[] Minutes;
    
    	public static BaseTime[] Days;
    
    	static void Main(string[] args)
    	{
    		Minutes = Enumerable.Range(1, 10000000).Select(n =&gt; (BaseTime) new Minute { Source = n }).ToArray();
