FROM 
(SELECT user_id, id_o, price,
CASE WHEN price &lt; 100 THEN &#39;3&#39;
     WHEN price &gt;= 100 AND price &lt; 500 THEN &#39;2&#39;
     ELSE &#39;3&#39;
END AS M,

CASE WHEN TIMESTAMPDIFF(DAY, o_date, DATE(&#39;2019-01-01&#39;)) &lt; 30 THEN &#39;1&#39;
     WHEN TIMESTAMPDIFF(DAY, o_date, DATE(&#39;2019-01-01&#39;)) &gt;= 30 AND
