    $(&quot;#s_to&quot;).tagsinput({
        tagClass: &#39;uk-badge&#39;,
        typeaheadjs: {
            source: function(query) {
                console.log(query);
                url = &quot;&lt;?php echo base_url(); ?&gt;index.php/&lt;?php echo $loc_pts; ?&gt;/ajax_email&quot;;
                var s_to = extractLast(query);
                ajax_status = &quot;fail&quot;;
                var toReturn = [];
                $.ajax({
                    url: url,
                    method: &quot;POST&quot;,
                    data: {
                        s_to: s_to
                    },
                    async: false,
                    dataType: &quot;json&quot;,
                    success: function(json) {
                        toReturn = json.s_to;
                    }
                });
                /* This is the correct scope to return the array */
                return toReturn;
            }
        }
    });

