      &lt;w:tblPr&gt;&lt;w:tblStyle w:val=&quot;TableGrid&quot;/&gt;
               &lt;w:tblW w:w=&quot;5000&quot; w:type=&quot;pct&quot;/&gt;
               &lt;w:tblLayout w:type=&quot;fixed&quot;/&gt;
               &lt;w:tblLook w:val=&quot;04A0&quot; w:firstRow=&quot;1&quot; w:lastRow=&quot;0&quot; w:firstColumn=&quot;1&quot; w:lastColumn=&quot;0&quot; w:noHBand=&quot;0&quot; w:noVBand=&quot;1&quot;/&gt;
      &lt;/w:tblPr&gt;
