    function collectKeys( $key, $post, $in ){
        if(!isset($in[$post[$key]])) {
            $in[$post[$key]] = [];
        }
        if ( is_array( $post[$key] ) ) {
            foreach( $post[$key] as $t =&gt; $d) {
                foreach( $post as $k =&gt; $v ) {
                    if ( $post[$k] !== $key ) {
                        if ( is_array( $post[$k] ) ) {
                            foreach( $post[$k] as $d =&gt; $c ) {
                                $in[$post[$key][$t]][] = $c;
                            }
                        } else {
                            $in[$post[$key][$t]][] = $v;
                        }
                    }
                }
            }
        }
        return $in;
    }

