&lt;!-- language: lang-js --&gt;
    var strs = [&#39;Foo&#39;, &#39;Foo Bar B&#39;, &#39;Laura Torres Bermudez&#39;, &#39;Maria Fernanda Gonzales Lopez&#39;];
    strs = strs.map(str =&gt; str.replace(/^(\w+\W+\w+\W+\b)\w?|(?!^)(\W*)\w/gy, &#39;$1$2*&#39;));
