function recursivePlus(min, max, collection) {
	if (!collection) {
		collection = [];
		collection.push(min);
		return recursivePlus(++min, max, collection);
	} else {
		if (min &lt;= max) {
			collection.push(min);
			return recursivePlus(++min, max, collection);
		} else {
			return collection;
		}	
	}
}
