    function helper(map, currString) {
        let sum = 0;
        for (let character in map) {
            if (map[character] !== 0) {
                let newString = currString + character;            
                sum++;
                
                map[character] = map[character] - 1;
                sum += helper(map, newString);
                map[character] = map[character] + 1;

                console.log(&#39;completed string: &#39;, newString);
            }
        }

