// one.js
const { fork } = require(&#39;child_process&#39;);

const p = fork(require.resolve(&#39;./two.js&#39;));
let exited = false;
setInterval(() =&gt; {
  if (!exited) {
    p.send(&#39;hello&#39;, undefined, undefined, (e) =&gt; {
      if (e !== null) {
        console.log(&#39;got err!&#39;, e);
        exited = true;
      }
    });
  }
}, 1);
