    function check(array) {
        const result = {x: null, y: 0};
        for (const entry of array) {
            if (result.x === null) {
                result.x = entry.x;
            } else if (result.x !== entry.x) {
                result.x = undefined;
            }
            result.y += entry.y;
        }
        console.log(result);
    }
    check([
