    function syntaxError(syntax) {
        const
            isOpening = c =&gt; /[&lt;[{(]/.test(c),
            isClosing = c =&gt; /[&gt;\]})]/.test(c),
            open = { &#39;&gt;&#39;: &#39;&lt;&#39;, &#39;]&#39;: &#39;[&#39;, &#39;}&#39;: &#39;{&#39;, &#39;)&#39;: &#39;(&#39; };

        var stack = [],
            index,
            finished = Array
                .from(syntax)
                .every((c, i) =&gt; {
                    var temp = stack[stack.length - 1];
                    if (isOpening(c)) {
                        if (temp &amp;&amp; temp.c === c) {
                            temp.indices.push(i);
                        } else {
                            stack.push({ c, indices: [i] });
                        }
                        return true;
                    }
                    if (isClosing(c)) {
