        // Explicitly shift the points up for the new segment points for the new segment
        points = [points[3], points[4], .zero, .zero, .zero]
        pointCounter = 1
        setNeedsDisplay()
    }

    override func touchesEnded(_ touches: Set&lt;UITouch&gt;, with event: UIEvent?) {
        cacheImage()
        setNeedsDisplay()
        path.removeAllPoints()
        pointCounter = 0
