    	version=&quot;1.0&quot;&gt;
    
      &lt;xsl:output method=&quot;html&quot; indent=&quot;yes&quot; doctype-system=&quot;about:legacy-doctype&quot;/&gt;
    
      &lt;xsl:template match=&quot;/&quot;&gt;
        &lt;html&gt;
          &lt;head&gt;
            &lt;title&gt;.NET XSLT Fiddle Example&lt;/title&gt;
          &lt;/head&gt;
          &lt;body&gt;
            &lt;xsl:apply-templates/&gt;
          &lt;/body&gt;
        &lt;/html&gt;
      &lt;/xsl:template&gt;
      
      &lt;xsl:template match=&quot;list[lang(&#39;en&#39;)]&quot;&gt;
          &lt;ul&gt;
              &lt;xsl:apply-templates select=&quot;word&quot;&gt;
                  &lt;xsl:sort select=&quot;.&quot;/&gt;
              &lt;/xsl:apply-templates&gt;
          &lt;/ul&gt;
      &lt;/xsl:template&gt;
      
      &lt;xsl:template match=&quot;word&quot;&gt;
          &lt;li&gt;
