        &lt;/Words&gt;
      &lt;/Lexicon&gt;
     &lt;/xsl:variable&gt;
     
     &lt;xsl:variable name=&quot;vCompositeWords&quot; as=&quot;element()*&quot;&gt;
       &lt;xsl:apply-templates select=&quot;$vDocEn/*/Words&quot; mode=&quot;combine&quot;&gt;
         &lt;xsl:with-param name=&quot;pWords2&quot; select=&quot;$vDocBG/*/Words&quot;/&gt;
