    public class BasicExample extends JFrame {
      private JTable t;
      private DefaultTableModel dtm;
    
      public BasicExample() {
        init();
      }
    
      private void init() {
        dtm = new DefaultTableModel(new String[][] { { &quot;a&quot;, &quot;b&quot; }, { &quot;c&quot;, &quot;d&quot; } },
            new String[] { &quot;A&quot;, &quot;B&quot; });
        t = new JTable(dtm);
    
