You can [groupBy](https://lodash.com/docs/#groupBy) position and then select the ones with the highest score from each group. Below is an algorithm for that in javascript:

&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    function makePlayer(name, position, score) {
      return {name, position, score}
    }

    const players = [
      makePlayer(&#39;A&#39;, &#39;QB&#39;, 10),
      makePlayer(&#39;B&#39;, &#39;QB&#39;, 12),
      makePlayer(&#39;C&#39;, &#39;RB&#39;, 14),
      makePlayer(&#39;D&#39;, &#39;RB&#39;, 5),
      makePlayer(&#39;E&#39;, &#39;RB&#39;, 7),
      makePlayer(&#39;F&#39;, &#39;WR&#39;, 14),
      makePlayer(&#39;G&#39;, &#39;WR&#39;, 17),
      makePlayer(&#39;H&#39;, &#39;WR&#39;, 21),
      makePlayer(&#39;I&#39;, &#39;TE&#39;, 4),
      makePlayer(&#39;J&#39;, &#39;TE&#39;, 1)
    ]

    // From https://stackoverflow.com/questions/14446511/most-efficient-method-to-groupby-on-an-array-of-objects
    function groupBy(xs, key) {
      return xs.reduce(function(rv, x) {
        (rv[x[key]] = rv[x[key]] || []).push(x);
        return rv;
      }, {})
    }

    const groupedByPosition = Object.values(groupBy(players, &#39;position&#39;));

    const result = groupedByPosition.map(players =&gt; {
      return players.reduce((previous, current) =&gt; {
        return current.score &gt; previous.score ? current : previous
      })
    })

    console.log(&#39;result&#39;, result)

&lt;!-- end snippet --&gt;

