    // Objective : To make the javascript file of a web app for tech trivia that will have a buzzer system  // that will display the time it was pressed and play a sound. This file will make the page dynamic.


    //Variable for the bleep sound :
    var bleep = new Audio();

    //Giving it the source to the audio file :
    bleep.src = &quot;http://devangad.000webhostapp.com/siren.mp3&quot;;

    //A variable to check the state whether a button has been clicked or not :
    var clickedon = false;


    // A function to play the sound and display the exact time it was pressed :
    function datetime(){
        var dt = new Date();
        var hours = dt.getHours() ; // gives the value in 24 hours format
        var AmOrPm = hours &gt;= 12 ? &#39;pm&#39; : &#39;am&#39;;
        hours = (hours % 12) || 12;
        var minutes = dt.getMinutes() ;
        var seconds = dt.getSeconds();
        var milseconds = dt.getMilliseconds();
        var finalTime =  hours + &quot; : &quot; + minutes + &quot; : &quot; +seconds+ &quot; : &quot; +milseconds + &quot; &quot;+AmOrPm;
        console.log(finalTime);
        if (clickedon != true){
          bleep.play();
          document.getElementById(&quot;Time&quot;).innerHTML = document.getElementById(&quot;Time&quot;).innerHTML + finalTime;
          clickedon = true;
        }
    }

    // A function to reset the time and reset the sound start point to 0.0 seconds :
    function datetimereset(){
        document.getElementById(&quot;Time&quot;).innerHTML = &quot;Buzzer Time &quot;;
        bleep.pause();
        bleep.currentTime = 0;
        clickedon = false;
    }

