std::string decode_url(const std::string&amp; path)
{
  std::stringstream decoded;
  for (std::size_t i = 0; i &lt; path.size(); i++)
  {
    if (path[i] != &#39;%&#39;)
    {
      if (path[i] == &#39;+&#39;)
        decoded &lt;&lt; &#39; &#39;;
      else
        decoded &lt;&lt; path[i];
    }
    else
    {
      unsigned int j;
      sscanf(path.substr(i + 1, 2).c_str(), &quot;%x&quot;, &amp;j);
      decoded &lt;&lt; static_cast&lt;char&gt;(j);
      i += 2;
    }
  }
  return decoded.str();
}

