                val round = RoundedBitmapDrawableFactory.create(resources, thumbnail)
                round.isCircular = true
                imageview!!.setImageDrawable(round)
                saveImage(thumbnail)
                Toast.makeText(this@MainActivity, &quot;Image Saved!&quot;, Toast.LENGTH_SHORT).show()
    
                }
