    			array[k++] = sub3[i3++];
    		else if(sub4[i4] &lt;= sub1[i1] &amp;&amp; sub4[i4] &lt;= sub2[i2] &amp;&amp; sub4[i4] &lt;= sub3[i3])
    			array[k++] = sub4[i4++];
    	}
    	while(i1&lt;subArraySize)
