            % goldSearch minimize the function  
            function [ xMin, fMin] = goldSearch(funcname, a, b, tol) 

            % a is the lower bound, b is the upper bound
            % default tolerance 
            if nargin &lt;4 
                tol=1.0e-6; 
            end

            % golden ratio value
            R=(sqrt(5)-1)/2; 
            C=1 - R; 

            % initial try and error
            x1=R*a+ C*b; 
            x2=C*a +R*b; 

            % get their function evaluation
            f1=feval(funcname,x1); 
            f2=feval(funcname,x2); 


            while (b-a)&gt;tol
               % update upper bound 
               if f1&gt;f2 
                   a = x1; x1 = x2; f1 = f2; 
                   x2 = C*a + R*b; 
                   f2= feval(funcname,x2); 
               else 
               % update lower bound
                   b = x2; x2 = x1; f2 = f1; 
                   x1= R*a + C*b; 
                   f1= feval(funcname,x1); 
               end
            end

                if f1&lt; f2 
                   fMin=f1; xMin=x1; 
                else 
                   fMin=f2; xMin=x2; 
                end 



            end 

end

