              &lt;/baseAddresses&gt;
            &lt;/host&gt;
          &lt;/service&gt;
        &lt;/services&gt;
        &lt;bindings&gt;
          &lt;netTcpBinding&gt;
            &lt;binding name=&quot;mybinding&quot;&gt;
              &lt;security mode=&quot;Transport&quot;&gt;
                &lt;transport clientCredentialType=&quot;None&quot;&gt;&lt;/transport&gt;
              &lt;/security&gt;
            &lt;/binding&gt;
          &lt;/netTcpBinding&gt;
        &lt;/bindings&gt;
        &lt;behaviors&gt;
