var randomSet: [[String:String]] = [[&quot;myImage&quot;:&quot;1-image&quot;, &quot;myText&quot;:&quot;text for 1&quot;], [&quot;myImage&quot;:&quot;2-image&quot;, &quot;myText&quot;:&quot;text for 2&quot;], [&quot;myImage&quot;:&quot;3-image&quot;, &quot;myText&quot;:&quot;text for 3&quot;], [&quot;myImage&quot;:&quot;4-image&quot;, &quot;myText&quot;:&quot;text for 4&quot;], [&quot;myImage&quot;:&quot;5-image&quot;, &quot;myText&quot;:&quot;text for 5&quot;]]

