    	private String translate(String raw) {
    		String translated = &quot;&quot;;
    		boolean wasLastCharacterVowel = false;  // 
    		
    		for(char c:raw.toCharArray()) {
    			if(isVowel(c)) {
    				wasLastCharacterVowel = true;
    			} else {
    				wasLastCharacterVowel = false;
    			}
