    struct qnode* temp = (struct qnode*)malloc(sizeof(struct qnode));
    temp-&gt;data = x;
    temp-&gt;next = NULL;
    head = temp;
    }
    else{
    struct qnode* temp = (struct qnode*)malloc(sizeof(struct qnode));
    temp-&gt;data = x;
    temp-&gt;next = head;
    head = temp;
    }
    }
    
    struct node* pop(){
    struct node* r;
    if(head == NULL){
        return NULL;
    }
    else{
     struct qnode* pt;
     pt = head;
     head = head-&gt;next;
     r = pt-&gt;data;
     free(pt);
     return r;
    }
    }
    
    
    // dequeue function.
    
    struct node* pop(){
    struct node* r;
    if(head == NULL){
        return NULL;
    }
    else{
     struct qnode* pt;
     pt = head;
     head = head-&gt;next;
     r = pt-&gt;data;
     free(pt);
     return r;
    }
    }
    
    
    
    // Function to print tree in level order.
    
    void leorder(){
    struct node* popped;
    popped = pop();
    printf(&quot;%d &quot;,popped-&gt;data);
    if(popped != NULL){
        if(popped-&gt;left != NULL){
            insertq(popped-&gt;left);
        }
    
        if(popped-&gt;right != NULL){
            insertq(popped-&gt;right);
        }
        leorder();
    }
    }

