    function getParts(string, parts) {

        function collectParts(index, parts) {
            if (index === string.length) {
                result.push(parts);
                return;
            }
            if (!indices[index]) return;
            indices[index].forEach(s =&gt; collectParts(index + s.length, [...parts, s]));
