    #include &lt;iostream&gt;
    
    void foo(std::pair&lt;int,int&gt; x) {
        static std::map&lt;std::pair&lt;int,int&gt;,int&gt; lookup_table{ 
            { { 1,1 }, 1},
            { { 2,3 }, 42}
        };
        std::cout &lt;&lt; lookup_table[x] &lt;&lt; &#39;\n&#39;;
        // or 
