    x = 3 * uint64(10)^n;
    pi = x;
    
    tic
    ctr = 0
    while x &gt; 1/2
        ctr = ctr + 1;
        x = x * k / ((k + 1) * 4);
        pi = pi + x /(k + 2);
        k = k +2;
    end
    fix(pi)
    toc

