package medicare.model;

import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import medicare.util.DBUtil;

import java.sql.ResultSet;
import java.sql.SQLException;

public class ClientDAO {

    //*******************************
    //SELECT an Client
    //*******************************
    public static Client searchClient (String clntId) throws SQLException, ClassNotFoundException {
        String selectStmt = &quot;SELECT * FROM client WHERE Id=&quot;+clntId;

        try {
            //Get ResultSet from dbExecuteQuery method
            ResultSet rsClnt = DBUtil.dbExecuteQuery(selectStmt);

            //Send ResultSet to the getClientFromResultSet method and get client object
            Client client = getClientFromResultSet(rsClnt);

            //Return client object
            return client;
        } catch (SQLException e) {
            System.out.println(&quot;While searching an client with &quot; + clntId + &quot; id, an error occurred: &quot; + e);
            //Return exception
            throw e;
        }
    }

    //Use ResultSet from DB as parameter and set Client Object&#39;s attributes and return client object.
    private static Client getClientFromResultSet(ResultSet rs) throws SQLException
    {
        Client clnt = null;
        if (rs.next()) {
            clnt = new Client();
            clnt.setClientId(rs.getInt(&quot;Id&quot;));
            clnt.setVoornaam(rs.getString(&quot;Voornaam&quot;));
            clnt.setAchternaam(rs.getString(&quot;Achternaam&quot;));
            clnt.setGeboortedatum(rs.getDate(&quot;Geboortedatum&quot;));
            clnt.setLengte(rs.getInt(&quot;Lengte&quot;));
            clnt.setGewicht(rs.getInt(&quot;Gewicht&quot;));
            clnt.setTelefoonnr(rs.getString(&quot;Telefoonnr&quot;));
            clnt.setICE1(rs.getString(&quot;Noodnr1&quot;));
            clnt.setICE2(rs.getString(&quot;Noodnr2&quot;));
            clnt.setExtra(rs.getString(&quot;Extra&quot;));
        }
        return clnt;
    }

    //*******************************
    //SELECT Clients
    //*******************************
    public static ObservableList&lt;Client&gt; searchClients() throws SQLException, ClassNotFoundException {
        //Declare a SELECT statement
        String selectStmt = &quot;SELECT * FROM client&quot;;

        //Execute SELECT statement
        try {
            //Get ResultSet from dbExecuteQuery method
            ResultSet rsClnts = DBUtil.dbExecuteQuery(selectStmt);

            //Send ResultSet to the getClientList method and get client object
            ObservableList&lt;Client&gt; clntList = getClientList(rsClnts);

            //Return client object
            return clntList;
        } catch (SQLException e) {
            System.out.println(&quot;SQL select operation has been failed: &quot; + e);
            //Return exception
            throw e;
        }
    }

    //Select * from clients operation
    private static ObservableList&lt;Client&gt; getClientList(ResultSet rs) throws SQLException, ClassNotFoundException {
        //Declare a observable List which comprises of Client objects
        ObservableList&lt;Client&gt; clntList = FXCollections.observableArrayList();

        while (rs.next()) {
            Client clnt = new Client();
            clnt = new Client();
            clnt.setClientId(rs.getInt(&quot;Id&quot;));
            clnt.setVoornaam(rs.getString(&quot;Voornaam&quot;));
            clnt.setAchternaam(rs.getString(&quot;Achternaam&quot;));
            clnt.setGeboortedatum(rs.getDate(&quot;Geboortedatum&quot;));
            clnt.setLengte(rs.getInt(&quot;Lengte&quot;));
            clnt.setGewicht(rs.getInt(&quot;Gewicht&quot;));
            clnt.setTelefoonnr(rs.getString(&quot;Telefoonnr&quot;));
            clnt.setICE1(rs.getString(&quot;Noodnr1&quot;));
            clnt.setICE2(rs.getString(&quot;Noodnr2&quot;));
            clnt.setExtra(rs.getString(&quot;Extra&quot;));
            //Add client to the ObservableList
            clntList.add(clnt);
        }
        //return clntList (ObservableList of Clients)
        return clntList;
    }

    //*************************************
    //UPDATE an client&#39;s email address
    //*************************************
    public static void updateClntEmail (String clntId, String clntExtra) throws SQLException, ClassNotFoundException {
        //Declare a UPDATE statement
        String updateStmt =&quot; UPDATE client SET Extra = &#39;&quot; + clntExtra + &quot;WHERE Id= &quot;+ clntId;

        //Execute UPDATE operation
        try {
            DBUtil.dbExecuteUpdate(updateStmt);
        } catch (SQLException e) {
            System.out.print(&quot;Error occurred while UPDATE Operation: &quot; + e);
            throw e;
        }
    }

    //*************************************
    //DELETE an client
    //*************************************
    public static void deleteClntWithId (String clntId) throws SQLException, ClassNotFoundException {
        //Declare a DELETE statement
        String updateStmt = &quot;DELETE FROM client WHERE Id =&quot;+clntId;

        //Execute UPDATE operation
        try {
            DBUtil.dbExecuteUpdate(updateStmt);
        } catch (SQLException e) {
            System.out.print(&quot;Error occurred while DELETE Operation: &quot; + e);
            throw e;
        }
    }

    //*************************************
    //INSERT an client
    //*************************************
    public static void insertClnt (String voornaam, String achternaam, String dob, int lengte, int gewicht, String nr, String ICE1, String ICE2, String extra) throws SQLException, ClassNotFoundException {
        //Declare a DELETE statement
        String updateStmt = &quot;INSERT INTO client (Id, Voornaam, Achternaam, Geboortedatum, Lengte, Gewicht, Telefoonnr, Noodnr1, Noodn2, Extra) &quot;
                + &quot;VALUES (null, &#39;&quot;+voornaam+&quot;&#39;, &#39;&quot;+achternaam+&quot;&#39;, &#39;&quot;+dob+&quot;&#39;, SYSDATE, &#39;&quot;+lengte+&quot;&#39;, &#39;&quot;+gewicht+&quot;&#39;, &#39;&quot;+nr+&quot;&#39;, &#39;&quot;+ICE1+&quot;&#39;, &#39;&quot;+ICE2+&quot;&#39;, &#39;&quot;+extra+&quot;&#39;);&quot;;

        //Execute DELETE operation
        try {
            DBUtil.dbExecuteUpdate(updateStmt);
        } catch (SQLException e) {
            System.out.print(&quot;Error occurred while DELETE Operation: &quot; + e);
            throw e;
        }
    }
}
