package medicare.Controllers;

import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.concurrent.Task;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.TableColumn;
import javafx.scene.control.TableView;
import javafx.scene.control.TextArea;
import javafx.scene.control.TextField;
import medicare.model.Client;
import medicare.model.ClientDAO;

import java.sql.Date;
import java.sql.SQLException;
import java.time.format.DateTimeFormatter;
import java.util.List;
import java.util.concurrent.Executor;
import java.util.concurrent.Executors;
import javafx.scene.control.DatePicker;

/**
 * Created by ONUR BASKIRT on 23.02.2016.
 */
public class ClientController {

    @FXML private TextField clntIdText;
    @FXML private TextArea resultArea;
    @FXML private TextField newEmailText;
    @FXML private TextField txtVoornaam;
    @FXML private TextField txtAchternaam;
    @FXML private DatePicker txtGeboortedatum;
    @FXML private TextField txtLengte;
    @FXML private TextField txtGewicht;
    @FXML private TextField txtNr;
    @FXML private TextField txtICE1;
    @FXML private TextField txtICE2;
    @FXML private TextField txtExtra;
    @FXML private TableView ClientTable;
    @FXML private TableColumn&lt;Client, Integer&gt; clntIdColumn;
    @FXML private TableColumn&lt;Client, String&gt; clntVoornaamColumn;
    @FXML private TableColumn&lt;Client, String&gt; clntAchternaamColumn;
    @FXML private TableColumn&lt;Client, Date&gt; clntDOBColumn;
    @FXML private TableColumn&lt;Client, Integer&gt; clntLengteColumn;
    @FXML private TableColumn&lt;Client, Integer&gt; clntGewichtColumn;

    //For MultiThreading
    private Executor exec;

    //Initializing the controller class.
    //This method is automatically called after the fxml file has been loaded.

    @FXML private void initialize () {
        /*
        The setCellValueFactory(...) that we set on the table columns are used to determine
        which field inside the Client objects should be used for the particular column.
        The arrow -&gt; indicates that we&#39;re using a Java 8 feature called Lambdas.
        (Another option would be to use a PropertyValueFactory, but this is not type-safe
        We&#39;re only using StringProperty values for our table columns in this example.
        When you want to use IntegerProperty or DoubleProperty, the setCellValueFactory(...)
        must have an additional asObject():
        */

        //For multithreading: Create executor that uses daemon threads:
        exec = Executors.newCachedThreadPool((runnable) -&gt; {
            Thread t = new Thread (runnable);
            t.setDaemon(true);
            return t;
        });

        clntIdColumn.setCellValueFactory(cellData -&gt; cellData.getValue().ClientIdProperty().asObject());
        clntVoornaamColumn.setCellValueFactory(cellData -&gt; cellData.getValue().voornaamProperty());
        clntAchternaamColumn.setCellValueFactory(cellData -&gt; cellData.getValue().achternaamProperty());
        clntDOBColumn.setCellValueFactory(cellData -&gt; cellData.getValue().dobProperty());
        clntLengteColumn.setCellValueFactory(cellData -&gt; cellData.getValue().lengteProperty().asObject());
        //clntGewichtColumn.setCellValueFactory(cellData -&gt; cellData.getValue().gewichtProperty().asObject());
    }


    //Search an Client
    @FXML private void searchClient (ActionEvent actionEvent) throws ClassNotFoundException, SQLException {
        try {
            //Get Client information
            Client clnt = ClientDAO.searchClient(clntIdText.getText());
            //Populate Client on TableView and Display on TextArea
            populateAndShowClient(clnt);
        } catch (SQLException e) {
            e.printStackTrace();
            resultArea.setText(&quot;Error occurred while getting Client information from DB.\n&quot; + e);
            throw e;
        }
    }

    //Search all Clients
    @FXML private void searchClients(ActionEvent actionEvent) throws SQLException, ClassNotFoundException {
        try {
            //Get all Clients information
            ObservableList&lt;Client&gt; clntData = ClientDAO.searchClients();
            //Populate Clients on TableView
            populateClients(clntData);
        } catch (SQLException e){
            System.out.println(&quot;Error occurred while getting Clients information from DB.\n&quot; + e);
            throw e;
        }
    }

    //Populate Clients for TableView with MultiThreading (This is for example usage)
    private void fillClientTable(ActionEvent event) throws SQLException, ClassNotFoundException {
        Task&lt;List&lt;Client&gt;&gt; task = new Task&lt;List&lt;Client&gt;&gt;(){
            @Override
            public ObservableList&lt;Client&gt; call() throws Exception{
                return ClientDAO.searchClients();
            }
        };
    }

    //Populate Client
    @FXML private void populateClient (Client clnt) throws ClassNotFoundException {
        //Declare and ObservableList for table view
        ObservableList&lt;Client&gt; clntData = FXCollections.observableArrayList();
        //Add Client to the ObservableList
        clntData.add(clnt);
        //Set items to the ClientTable
        ClientTable.setItems(clntData);
    }

    //Set Client information to Text Area
    @FXML private void setClntInfoToTextArea ( Client clnt) {
        resultArea.setText(&quot;Voornaam: &quot; + clnt.getVoornaam() + &quot;\n&quot; +
                &quot;Achternaam: &quot; + clnt.getAchternaam());
    }

    //Populate Client for TableView and Display Client on TextArea
    @FXML
    private void populateAndShowClient(Client clnt) throws ClassNotFoundException {
        if (clnt != null) {
            populateClient(clnt);
            setClntInfoToTextArea(clnt);
        } else {
            resultArea.setText(&quot;Deze Cli&#235;nt bestaat niet!\n&quot;);
        }
    }

    //Populate Clients for TableView
    @FXML private void populateClients (ObservableList&lt;Client&gt; clntData) throws ClassNotFoundException {
        //Set items to the ClientTable
        ClientTable.setItems(clntData);
    }

    //Update Client&#39;s email with the email which is written on newEmailText field
    @FXML private void updateClientEmail (ActionEvent actionEvent) throws SQLException, ClassNotFoundException {
        try {
            ClientDAO.updateClntEmail(clntIdText.getText(),newEmailText.getText());
            resultArea.setText(&quot;Email has been updated for, Client id: &quot; + clntIdText.getText() + &quot;\n&quot;);
        } catch (SQLException e) {
            resultArea.setText(&quot;Problem occurred while updating email: &quot; + e);
        }
    }

    //Insert an Client to the DB
    @FXML private void insertClient (ActionEvent actionEvent) throws SQLException, ClassNotFoundException {
        try {
            String voornaam = txtVoornaam.getText();
            String achternaam = txtAchternaam.getText();
            String dob = txtGeboortedatum.getValue().format(DateTimeFormatter.ofPattern(&quot;dd-M-yyyy&quot;));
            int lengte = Integer.parseInt(txtLengte.getText());
            int gewicht = Integer.parseInt(txtGewicht.getText());
            String nr = txtNr.getText();
            String ICE1 = txtICE1.getText();
            String ICE2 = txtICE2.getText();
            String Extra = txtExtra.getText();
            ClientDAO.insertClnt(voornaam, achternaam, dob, lengte, gewicht, nr, ICE1, ICE2, Extra);
            resultArea.setText(&quot;Client inserted! \n&quot;);
        } catch (SQLException e) {
            resultArea.setText(&quot;Problem occurred while inserting Client &quot; + e);
            throw e;
        }
    }

    //Delete an Client with a given Client Id from DB
    @FXML private void deleteClient (ActionEvent actionEvent) throws SQLException, ClassNotFoundException {
        try {
            ClientDAO.deleteClntWithId(clntIdText.getText());
            resultArea.setText(&quot;Client deleted! Client id: &quot; + clntIdText.getText() + &quot;\n&quot;);
        } catch (SQLException e) {
            resultArea.setText(&quot;Problem occurred while deleting Client &quot; + e);
            throw e;
        }
    }
}
