int gameLogicLeft(int fIndex, int sideToJump, string terrain)
{
	int blocksToJump = sideToJump;
	int jumps = 0;

	while (1) {
		if (fIndex - blocksToJump &gt;= 0) {
			if (terrain[fIndex - blocksToJump] == &#39;#&#39;) {
				blocksToJump += sideToJump;
				++jumps;
				continue;
			}
			else if (terrain[fIndex - blocksToJump] == &#39;.&#39;) {
				jumps = 0;
				break;
			}
			else
				break;
		}
		else
			break;
	}
	return jumps;
}
