		     Executors.newScheduledThreadPool(1);

	public static Map&lt;ScheduledFuture&lt;Jobs&gt;, Jobs&gt; listLinked = new HashMap&lt;&gt;();
	public static Map&lt;Jobs, ScheduledFuture&lt;Jobs&gt;&gt; listLinkedInverse = new HashMap&lt;&gt;();
