    		this.job = job;
    	}
    	public void run() {
    			JobParameters jobParameters = new JobParametersBuilder().addLong(&quot;time&quot;, System.currentTimeMillis())
    					.addString(&quot;param&quot;, job.getParam()).toJobParameters();
    			try {
