      } else if (workbook instanceof XSSFWorkbook) {
       out = new FileOutputStream(&quot;CreateExcelCellStyleRedAndGreen.xlsx&quot;);
      }
      workbook.write(out);
      out.close();
      workbook.close();
     }
    }

