          {
            &quot;logger&quot;: &quot;*&quot;,
            &quot;minLevel&quot;: &quot;${MinLevelInfo}&quot;,
            &quot;writeTo&quot;: &quot;logconsole&quot;
          },
          {
            &quot;logger&quot;: &quot;*&quot;,
            &quot;minLevel&quot;: &quot;${MinLevelError}&quot;,
            &quot;writeTo&quot;: &quot;allfile&quot;
          }
