                }
                // This is the key modifier
                .navigationBarHidden(!detailShowing)
                .navigationBarTitle(&quot;Detail View&quot;, displayMode: .inline)
                .navigationBarItems(leading: Button(&quot;Close&quot;) {
                    self.detailShowing = false
                })
            }
