    		for (size_t j = 1; j &lt; n; ++j){
    			if (Distf[i][j] &lt; minValue){
    				minValue = Distf[i][j];
    			}
    		}
            //minRows.push_back(minValue) is a race condition!
    		minRows[i] = minValue;
