        async function fetchProduct() {
          const response = await fetch(&#39;http://myapi/product&#39; + productId);
          const json = await response.json();
          setProduct(json);
        }
    
        fetchProduct();
      }, [productId]); // ✅ Valid because our effect only uses productId
      // ...
    }

