            a.MakeNew(a.Latitude - b.Latitude, a.Longitude - b.Longitude);
        public static ICoordinate Plus(this ICoordinate a, ICoordinate b) =&gt;
            a.MakeNew(a.Latitude + b.Latitude, a.Longitude + b.Longitude);
    }

