    public class Worker extends Thread {
    
        private volatile boolean isAlive;
        private Semaphore sema = new Semaphore(1);
    
        public Worker() {
            try {
                sema.acquire();
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
    
        @Override
        public void run() {
            isAlive = true;
            sema.release();
            try {
                do {
                    //doSomething
                } while (true);
            } finally {
                isAlive = false;
            }
        }
    
        public void waitForSomething() {
            try {
                sema.acquire();
                if (!isAlive) {
                    throw new IllegalStateException(&quot;Worker is not alive&quot;);
                }
            } catch (InterruptedException e) {
                e.printStackTrace();
            } finally {
                sema.release();
            }
            //something
        }
    
        public void waitForStartOfDoingSomething() {
            //need to wait isAlive = true.
        }
    }

