        // check for id
        if (obj.hasOwnProperty(&#39;id&#39;)) {
            ids = [...ids, obj.id];
        }
    
        // check for children and do recursive check
        if (obj.hasOwnProperty(&#39;children&#39;) &amp;&amp; obj.children.length &gt; 0) {
            obj.children.forEach(child =&gt; {
                getIds(child);
            });
        }
    }
    
