        config.setDataSource(dataSource);
        config.setAllowPoolSuspension(true);
        config.setMinimumIdle(minIdle);
        pool = new HikariPool(config);
        HikariPool result = pool;
        if (result == null) {
            synchronized (this) {
                result = pool;
