    			b.logger.Println(&quot;f n&quot;, n, &quot;err&quot;, err)
    		}
    		if err != nil {
    			hasended = true
    		}
    		buf = buf[:n]
    		b.start -= int64(n)
    		if b.logger != nil {
    			b.logger.Println(&quot;g start&quot;, b.start)
    		}
    		if b.start &gt; 0 {
    			i := bytes.Index(buf, b.Sep)
    			if b.logger != nil {
    				b.logger.Println(&quot;h sep&quot;, i)
    			}
    			if i &gt; -1 {
    				b.start += int64(i)
    				buf = buf[i:]
    				if b.logger != nil {
    					b.logger.Println(&quot;i start&quot;, b.start)
    				}
    			}
    		}
    		if e := bytes.LastIndex(buf, search); e &gt; -1 {
    			return b.start + int64(e), nil
    		}
    	}
    
    	return -1, nil
    }
    
    func (b tailSearch) Index2(search []byte) (int64, error) {
    
    	if b.Buffer == nil {
    		b.Buffer = make([]byte, 1024, 1024)
    	}
    	buf := b.Buffer
    	blen := len(b.Buffer)
    
    	hasended := false
    	for !hasended {
    		if b.logger != nil {
    			b.logger.Println(&quot;a start&quot;, b.start)
    		}
    		offset := b.start - int64(blen)
    		if offset &lt; 0 {
    			offset = 0
    			hasended = true
    		}
    		_, err := b.F.Seek(offset, 0)
    		if err != nil {
    			hasended = true
    		}
    
    		n, err := b.F.Read(buf)
    		if b.logger != nil {
    			b.logger.Println(&quot;f n&quot;, n, &quot;err&quot;, err)
    		}
    		if err != nil {
    			hasended = true
    		}
    		buf = buf[:n]
    		b.start -= int64(n)
    
    		if b.logger != nil {
    			b.logger.Println(&quot;g start&quot;, b.start)
    		}
    
    		for i := 1; i &lt; len(search); i++ {
    			if bytes.HasPrefix(buf, search[i:]) {
    				e := i - len(search)
    				b.start += int64(e)
    				buf = buf[e:]
    			}
    		}
    		if b.logger != nil {
    			b.logger.Println(&quot;g start&quot;, b.start)
    		}
    
    		if e := bytes.LastIndex(buf, search); e &gt; -1 {
    			return b.start + int64(e), nil
    		}
    	}
    
    	return -1, nil
    }


